;) 54 mkdir -p themes/after-dark 55 wget -qO - https://registry.npmjs.org/after-dark/-/after-dark-&#34;$vers&#34;.tgz | tar --strip-components=1 -xz -C themes/after-dark 56 echo &#34;Version $versdownloaded to $SITE_DIR/themes/after-dark&#34; 57} 58 59download_module () { 60 [ -z &#34;$1&#34; ] &amp;&amp; { echo &#34;Error: Attempt to download undefined module&#34; &gt;&amp;2; exit 1; } 61 echo &#34;Downloading $1module for After Dark ...&#34; 62 meta=$(wget -qO - https://registry.npmjs.org/&#34;$1&#34;/latest) 63 vers=$(echo &#34;$meta&#34; | grep -oE &#34;\&#34;version\&#34;.*[^,]*,&#34; | cut -d &#39;,&#39; -f1 | cut -d &#39;:&#39; -f2 | tr -d &#39;&#34; &#39;) 64 mkdir -p themes/&#34;$1&#34; 65 wget -qO - https://registry.npmjs.org/&#34;$1&#34;/-/&#34;$1&#34;-&#34;$vers&#34;.tgz | tar --strip-components=1 -xz -C themes/&#34;$1&#34; 66 echo &#34;Version $versdownloaded to $SITE_DIR/themes/$1&#34; 67} 68 69configure_theme () { 70 echo &#34;Configuring basic After Dark theme settings ...&#34; 71 tee &#34;config.toml&#34; &gt; /dev/null &lt;&lt;TOML 72baseurl = &#34;https://domain.example&#34; # Controls base URL sitewide 73languageCode = &#34;en-US&#34; # Controls site language 74title = &#34;After Dark&#34; # Homepage title and page title suffix 75paginate = 11 # Number of posts to show before paginating 76copyright = &#34;Copyright &amp;copy; Copyright Owner. Licensed under &lt;a target=\&#34;_blank\&#34; rel=\&#34;external noopener license\&#34; href=\&#34;https://creativecommons.org/licenses/by-nd/4.0/\&#34;&gt;CC-BY-ND-4.0&lt;/a&gt;.&#34; # Optional, remove to suppress copyright notices 7778# Controls default theme and theme components 79theme = [ 80&#34;fractal-forest&#34;, # OBSD 81&#34;after-dark&#34; # AGPL-3.0-or-later 82] 8384disableLiveReload = false # Optional, set true to disable live reload 85enableRobotsTXT = true # Suggested, enable robots.txt file 86sectionPagesMenu = &#34;main&#34; # Enable menu system for lazy bloggers 8788[markup.goldmark.renderer] 89unsafe = true # Optional, allows HTML inside your CommonMark content 90[markup.tableOfContents] 91startLevel = 1 # Suggested, draws TOC using all heading levels 92endLevel = 6 # Suggested, draws TOC using all heading levels 93[markup.highlight] 94noClasses = false # Suggested, used for custom syntax highlighting 9596[params] 97description = &#34;&#34; # Suggested, controls default description meta 98author = &#34;&#34; # Optional, controls author name display on posts 99hide_author = false # Optional, set true to hide author name on posts 100disable_csp = false # Optional, set true to disable content security policy 101images = [ 102&#34;https://source.unsplash.com/collection/983219/2000x1322&#34; 103] # Suggested, controls default Open Graph images 104105[params.layout.menu.main] 106hidden = true # Optional, set false or remove to show section menu 107108[params.layout.footer] 109hidden = false # Optional, set true to hide footer 110111[params.modules.fractal_forest] 112enabled = true # Optional, set false to disable module 113decoders = [&#34;bpgdec8a&#34;] # Optional, 8-bit javascript decoder with animation 114TOML 115} 116 117update_archetypes () { 118 echo &#34;Updating the default content archetype ...&#34; 119 rm -f archetypes/default.md 120 cp themes/after-dark/archetypes/default.md archetypes 121} 122 123create_welcome_post () { 124 echo &#34;Creating welcome post ...&#34; 125 hugo new post/welcome.md 1&gt;/dev/null 126} 127 128serve_site () { 129 echo &#34;Starting site server ...&#34; 130 hugo serve --buildDrafts --navigateToChanged --port 1313 1&gt;/dev/null &amp; 131} 132 133generate_help_docs () { 134 echo &#34;Generating help documentation ...&#34; 135 THEME_PATH=themes/after-dark 136 meta_path=&#34;$THEME_PATH&#34;/data/npm 137 mkdir -p &#34;$meta_path&#34; &amp;&amp; echo &#34;$LATEST_META&#34; | tr &#39;\r\n&#39; &#39; &#39; &gt; &#34;$meta_path&#34;/latest.json 138 cd &#34;$THEME_PATH&#34;/docs &amp;&amp; mkdir themes &amp;&amp; ln -s ../.. &#34;$THEME_PATH&#34; 139 hugo new validate.md --kind validate 1&gt;/dev/null 140} 141 142serve_help () { 143 echo &#34;Starting help server ...&#34; 144 hugo serve --disableLiveReload --port 1414 1&gt;/dev/null &amp; 145} 146 147echo &#34;Welcome to the After Dark quick installer. Press CTRL-C at any time to abort.&#34; 148 149validate_hugo 150create_site_dir &#34;$1&#34; 151create_site 152download_theme 153update_archetypes 154download_module &#34;fractal-forest&#34; 155configure_theme 156create_welcome_post 157serve_site 158generate_help_docs 159serve_help 160 161YELLOW=&#39;\033[0;33m&#39; 162NC=&#39;\033[0m&#39; 163 164printf &#34;${YELLOW}Installation successful!${NC}\n&#34; 165echo &#34;Site created in $SITE_DIR_ABS&#34; 166echo &#34;Site server started at http://localhost:1313/&#34; 167echo &#34;To stop it run \&#34;kill \$(ps aux | awk &#39;/[h]ugo.*1313/ {print \$2}&#39;)\&#34;.&#34; 168echo &#34;Help server started at http://localhost:1414/&#34; 169echo &#34;To stop and restart it run \&#34;./themes/after-dark/bin/help\&#34;.&#34; 170echo &#34;Thank you for choosing After Dark.&#34;
